import { useState, useEffect } from "react";
import axios from "axios";
const API = process.env.REACT_APP_API_URL

const RelatedDrip = ({collection}) => {
    const { category } = collection
    const [related, setRelated] = useState([])

    const getRelatedItems = () => {
        return related.filter((drip) => drip?.category === category)
    }

    const filtered = getRelatedItems();

    useEffect(() => {
        axios.get(`${API}/drip`)
          .then((response) => {
            console.log(response.data)
            setRelated(response.data);
            console.log('calling func...');
            console.log(getRelatedItems());
          });
        }, []);

    return (
        <div>
             <h3>You also like</h3>
                {filtered.map((card) => <div key={card.id}> {card.name}</div>)}
        </div>
    );
};

export default RelatedDrip;
