import axios from "axios";
import { useState, useEffect } from "react";
import {  useParams, useNavigate } from "react-router-dom";
import HoverImage from "./HoverImage";
const API = process.env.REACT_APP_API_URL

const DripDetails = () => {
    const [collection, setCollection] = useState({});
    const [related, setRelated] = useState([])
    const { id } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        axios.get(`${API}/drip`)
          .then((response) => {
            console.log(response.data)
            setRelated(response.data);
          });
        }, []);

    useEffect(() => {
        axios.get(`${API}/drip/${id}`)
          .then((response) => {
            setCollection(response.data[0]);
          });
        }, [id, navigate]);

    return (
        <div className="wrapper">
            <div className="imageWrapper">
            <HoverImage 
            img_url={collection?.img_url} 
            img2_url={collection?.img2_url} 
            alt={`Image of the ${collection?.name}`} 
            height='100%' 
            width='100%'
            />
            </div>
            <div className="textWrapper">
                <h1>{collection?.name}</h1>
                <h2>${collection?.price}</h2>
                <p>{collection?.description}</p>
                <span>Available at {""}
                    <a href={collection?.product_url} target="_blank" rel="noreferrer" >{collection?.store_name}</a>
                </span>

            </div>
            <div className="relatedWrapper">
                <h3>You also like</h3>
                {related.filter((drip) => drip.category === collection?.category)}
            </div>
        </div>
    );
};

export default DripDetails;
