import axios from "axios";
import { useState, useEffect } from "react";
import {  useParams, useNavigate } from "react-router-dom";
import HoverImage from "./HoverImage";
const API = process.env.REACT_APP_API_URL

const DripDetails = () => {
    const [collection, setCollection] = useState({});
    const { id } = useParams();
    const navigate = useNavigate();
    const {img2_url, img_url, name, price, description, product_url, store_name} = collection


    useEffect(() => {
        axios.get(`${API}/drip/${id}`)
          .then((response) => {
            setCollection(response.data[0]);
          });
        }, [id, navigate]);

    return (
        <div className="wrapper">
            <div className="imageWrapper">
            <HoverImage 
            img_url={img_url} 
            img2_url={img2_url} 
            alt={`Image of the ${name}`} 
            height='100%' 
            width='100%'
            />
            </div>
            <div className="textWrapper">
                <h1>{name}</h1>
                <h2>${price}</h2>
                <p>{description}</p>
                <span>Available at {""}
                    <a href={product_url} target="_blank" rel="noreferrer" >{store_name}</a>
                </span>

            </div>
            <div className="relatedWrapper">

            </div>
        </div>
    );
};

export default DripDetails;
