import { Link } from "react-router-dom";
import HoverImage from "./HoverImage";

const Drip = ({ name, img_url, img2_url, price, id}) => {
    return (
        <div key={id}>

            <HoverImage img_url={img_url} img2_url={img2_url} alt={`Image of the ${name}`}></HoverImage>
            <p>{name}</p>
            <p>{price}</p>


        </div>
    );
};

export default Drip;

