import { useState, useEffect } from "react";
import Drip from './Drip'
import axios from "axios"
const API = process.env.REACT_APP_API_URL

const Drips = () => {
    const [collection, setCollection] = useState([])
    const [isLoading, setIsLoading] = useState(true);

    const sortData = (data) => {
        const newData = {};
        for (let i = 0; i < data.length; i++) {
          const currProduct = data[i];
          if (!newData[currProduct.category]) {
            newData[currProduct.category] = [];
          }
          newData[currProduct.product_type].push(currProduct);
        }
        return newData;
      }

    useEffect(() => {
        axios.get(`${API}/drip`)
            .then((res) => {
                setCollection(res.data);
                setIsLoading(false);
                console.log(res.data)
            })
            .catch(err=> console.log(err))
    },[])

    return (
        isLoading ? <h3> Loading... </h3> : 
        <div className="catalog"> 
        {collection.map((drip) => <Drip key={drip.id} drip={drip}/>)}
        <button
          alt={'Click here to load more products.'}
          onClick={()=>{}}
        >
          {'Show More'}
        </button>
        </div>
    );
};

export default Drips;
