import { useState, useEffect } from "react";
import axios from "axios";
import Drip from "./Drip";

const API = process.env.REACT_APP_API_URL;

function Drips() {
  const [drips, setDrips] = useState([]);
  useEffect(() => {
    axios
      .get(`${API}/drips`)
      .then((response) => setDrips(response.data))
      .catch((c) => console.warn("catch", c));
  }, []);

  return (
    <div className="drips">
      <section>
        <table>
          <thead>
            <tr>
              <th></th>
              <th>Take me there</th>
              <th>See this drip</th>
            </tr>
          </thead>
          <tbody>
            {drips.map((drip) => {
              return <Drip key={drip.id} drip={drip} />;
            })}
          </tbody>
        </table>
      </section>
    </div>
  );
}

export default Drips;
