import { useRef } from "react";

const HoverImage = ({ img_url, img2_url, alt}) => {
    const imageRef = useRef(null)

    return (
        <img
        src={img_url}
        ref={imageRef}
        width={'150px'}
        height={'150px'}
        onMouseOver={() => {
          imageRef.current.src = img2_url;
        }}
        onMouseOut={() => {
          imageRef.current.src = img_url;
        }}
      />
    );
};

export default HoverImage;
