import { useState } from "react";

const SectionWrapper = ({drip}) => {
    const [startIndex, setStartIndex] = useState(0)
    const [endIndex, setEndIndex] = useState(10)

    const updateIndexForSection = () => {
        if (drip.length > endIndex + 12) {
          setEndIndex(endIndex + 12);
          setStartIndex(startIndex + 12);
        }
    }
    return (
        <div>
      {drip.splice(startIndex, endIndex).map((product) => (
        <Product
          title={product.title}
          images={product.images}
          id={product.id}
          published_at={product.published_at}
          variants={product.variants}
        />
      ))}
      {endIndex < products.length ? (
        <ShowMoreButton
          alt={showMoreAltText}
          onClick={() => updateIndexForSection()}
        >
          {showMoreCopy}
        </ShowMoreButton>
      ) : null}
    </div>
    );
};

export default SectionWrapper;
